import React ,{useState , useEffect} from "react";
import ReactDOM  from "react-dom";
import axios from "axios";

const App =  () => {
    const [movies, setMovies] = useState([]);
    const [title , setTitle] = useState("");
    const [pageNumber , setPageNumber] = useState("");
    // const [pagePrev , setPagePrev] = useState("");
    // const [pageNext , setPageNext] = useState("");
    const [pageTotal , setPageTotal] = useState("");


    const getMovies = async (p) => {
        // const moviesRequest = await axios.get("http://www.omdbapi.com/?i=tt3896198&apikey=278ba782");

        const moviesRequest = await axios.get("http://www.omdbapi.com/?s=$harry&apikey=278ba782");
        setMovies(moviesRequest.data.Search);
    };
    const SearchTab = async (p) => {
        // const moviesRequest = await axios.get("http://www.omdbapi.com/?i=tt3896198&apikey=278ba782");
        if(p<1)p=1;
        if(p!=1&&p>pageTotal)p=pageTotal;
        const moviesRequest = await axios.get(`http://www.omdbapi.com/?s='${title}'&Page=${p}&apikey=278ba782`);
        setPageTotal(Math.ceil(moviesRequest.data.totalResults/10));
        setMovies(moviesRequest.data.Search);
    };


    useEffect( () =>{
        getMovies();
    }, []);

    return(
        <>
            <div>
                <input type="text" value={title} placeholder="Enter title" onChange={(e) => setTitle(e.target.value)} />
                <button className="button_main" onClick={()=>SearchTab(1)}>Search</button>
            { movies.length > 0 ? movies.map(movie => {
                return (
                    <p>{movie.Title}</p>
                )
            }): ''}
            </div>
            <div>
            <button className="button_main" onClick={()=>SearchTab(pageNumber-1)}> {'<-'} Previous</button>
            {pageNumber}
            <button className="button_main" onClick={()=>SearchTab(pageNumber+1)}>Next {'->'} </button>
            </div>
        </>
      )
}
ReactDOM.render(<App />,document.getElementById("root"));
