import React ,{useState , useEffect} from "react";
import ReactDOM  from "react-dom";
import axios from "axios";

const App =  () => {
    const [movies, setMovies] = useState([]);
    const [title , setTitle] = useState("harry");
    const [date , setDate ] = useState("");
    const [ids , setIds] = useState("");
    const [pageNumber , setPageNumber] = useState(1);
    // const [pagePrev , setPagePrev] = useState("");
    // const [pageNext , setPageNext] = useState("");
    const [pageTotal , setPageTotal] = useState(0);


    const getMovies = async (p) => {
        // const moviesRequest = await axios.get("http://www.omdbapi.com/?i=tt3896198&apikey=278ba782");

        const moviesRequest = await axios.get("http://www.omdbapi.com/?s=$harry&apikey=278ba782");
        setMovies(moviesRequest.data.Search);
        setPageTotal(Math.ceil(moviesRequest.data.totalResults/10));
    };
    const SearchTab = async (p) => {

        console.log(p);
        let flag=true;
        if(p<1){
            p=1;
            flag=false;
        }
        if(p!==1&&p>pageTotal){
            p=pageTotal;
            flag=false;
        }
        console.log(p);
        setPageNumber(p);
        if(flag){
            const moviesRequest = await axios.get(`http://www.omdbapi.com/?s=${title}&y=${date}&Page=${p}&apikey=278ba782`);
            setPageTotal(Math.ceil(moviesRequest.data.totalResults/10));
            moviesRequest?setMovies(moviesRequest.data.Search):setMovies("");
            // tt1201607
        }
    };


    useEffect( () =>{
        getMovies();
    }, []);

    return(
        <>
            <div>
                <input type="text" value={title} placeholder="Enter title" onChange={(e) => setTitle(e.target.value)} />
                <input type="number" value={date} placeholder="Enter release year" onChange={(e) => setDate(e.target.value)} />
                <input type="text" value={ids} placeholder="Enter release year" onChange={(e) => setIds(e.target.value)} />
                <button className="button_main" onClick={()=>SearchTab(1)}>Search</button>
            { movies.length > 0 ? movies.map(movie => {
                return (
                    <>
                        <p>{movie.Title}</p>
                        <img src="${movie.Poster}" alt={movie.Title}/>
                    </>
                )

            }): ''}
            {movies.length === 0 &&
                <>
                <p>No Result Found!!....</p>
                </>            
            }
            </div>
            <div>
            <button className="button_main" onClick={()=>{SearchTab(pageNumber-1);}}> {'<-'} Previous</button>
            {pageNumber}
            <button className="button_main" onClick={()=>{SearchTab(pageNumber+1);}}>Next {'->'} </button>
            </div>
        </>
      )
}
ReactDOM.render(<App />,document.getElementById("root"));
