import React ,{useState , useEffect} from "react";
import ReactDOM  from "react-dom";
import axios from "axios";

const App =  () => {
    const [movies, setMovies] = useState([]);
    const [title , setTitle] = useState("");

    const getMovies = async () => {
        // const moviesRequest = await axios.get("http://www.omdbapi.com/?i=tt3896198&apikey=278ba782");

        const moviesRequest = await axios.get("http://www.omdbapi.com/?s=$harry&apikey=278ba782");
        setMovies(moviesRequest.data.Search);
        console.log(movies);
    };
    const SearchTab = async () => {
        // const moviesRequest = await axios.get("http://www.omdbapi.com/?i=tt3896198&apikey=278ba782");

        const moviesRequest = await axios.get(`http://www.omdbapi.com/?s='${title}'&apikey=278ba782`);
        setMovies(moviesRequest.data.Search);
        console.log(movies);
    };


    useEffect( () =>{
        getMovies();
    }, []);

    return(
        <div>
            <input type="text" value={title} placeholder="Enter title" onChange={(e) => setTitle(e.target.value)} />
            <button className="button_main" onClick={SearchTab}>Search</button>
          { movies.length > 0 ? movies.map(movie => {
              return (
                <p>{movie.Title}</p>
              )
          }): ''}
        </div>
      )
}
ReactDOM.render(<App />,document.getElementById("root"));
